var socket = io();

socket.on("erreur", (socket_msg) => {
  $("#errorMessage").show().html(socket_msg);
});

socket.on("info", (socket_msg) => {
  $("#infoMessage").show().html(socket_msg);
});

$("#urlForm").submit(function (e) {
  e.preventDefault();
  if ($("#urlInput").val() != "" && $("#urlInput").val())
    socket.emit("url", $("#urlInput").val());
});
